cat > cmd/agent-server/main.go << 'EOF'
package main

import (
	"context"
	"fmt"
	"net/http"
	"os"
	"os/signal"
	"syscall"
	"time"

	"github.com/gin-contrib/cors"
	"github.com/gin-gonic/gin"
	"github.com/joho/godotenv"
	"github.com/sirupsen/logrus"

	"agentic-go/internal/agents"
	"agentic-go/internal/api"
)

func main() {
	// Load environment variables
	godotenv.Load()

	// Initialize logger
	logrus.SetLevel(logrus.DebugLevel)
	logrus.SetFormatter(&logrus.JSONFormatter{})

	// Initialize Gin
	if os.Getenv("GO_ENV") == "production" {
		gin.SetMode(gin.ReleaseMode)
	}

	router := gin.Default()

	// Configure CORS
	config := cors.DefaultConfig()
	config.AllowAllOrigins = true
	config.AllowMethods = []string{"GET", "POST", "PUT", "DELETE", "OPTIONS"}
	config.AllowHeaders = []string{"Origin", "Content-Type", "Accept", "Authorization"}
	router.Use(cors.New(config))

	// Initialize agent manager
	agentManager := agents.NewManager()

	// Initialize API routes
	api.SetupRoutes(router, agentManager)

	// Health check
	router.GET("/health", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{
			"status":  "healthy",
			"service": "agentic-go",
			"version": "0.1.0",
		})
	})

	// Start server
	srv := &http.Server{
		Addr:    ":8000",
		Handler: router,
	}

	go func() {
		logrus.Info("Starting Agentic Go server on :8000")
		if err := srv.ListenAndServe(); err != nil && err != http.ErrServerClosed {
			logrus.Fatalf("Server failed to start: %v", err)
		}
	}()

	// Graceful shutdown
	quit := make(chan os.Signal, 1)
	signal.Notify(quit, syscall.SIGINT, syscall.SIGTERM)
	<-quit
	logrus.Info("Shutting down server...")

	ctx, cancel := context.WithTimeout(context.Background(), 30*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logrus.Fatal("Server forced to shutdown:", err)
	}

	logrus.Info("Server exited")
}
EOF

cat > internal/agents/manager.go << 'EOF'
package agents

import (
	"context"
	"fmt"
	"sync"
	"time"

	"github.com/google/uuid"
	"github.com/sirupsen/logrus"

	"agentic-go/pkg/types"
)

// Agent interface defines the contract for all agents
type Agent interface {
	GetID() string
	GetName() string
	GetCapabilities() []string
	ProcessMessage(ctx context.Context, message types.AgentMessage) (*types.AgentResponse, error)
}

// Manager handles agent lifecycle and routing
type Manager struct {
	agents map[string]Agent
	mu     sync.RWMutex
}

// NewManager creates a new agent manager
func NewManager() *Manager {
	m := &Manager{
		agents: make(map[string]Agent),
	}

	// Register default Go agent
	goAgent := NewGoAgent("Default Go Agent")
	m.RegisterAgent(goAgent)

	return m
}

// RegisterAgent adds an agent to the manager
func (m *Manager) RegisterAgent(agent Agent) {
	m.mu.Lock()
	defer m.mu.Unlock()
	m.agents[agent.GetID()] = agent
	logrus.Infof("Registered agent: %s (%s)", agent.GetName(), agent.GetID())
}

// GetAgent retrieves an agent by ID
func (m *Manager) GetAgent(id string) (Agent, bool) {
	m.mu.RLock()
	defer m.mu.RUnlock()
	agent, exists := m.agents[id]
	return agent, exists
}

// ListAgents returns all registered agents
func (m *Manager) ListAgents() []Agent {
	m.mu.RLock()
	defer m.mu.RUnlock()

	agents := make([]Agent, 0, len(m.agents))
	for _, agent := range m.agents {
		agents = append(agents, agent)
	}
	return agents
}

// ProcessMessage routes a message to the appropriate agent
func (m *Manager) ProcessMessage(ctx context.Context, agentID string, message types.AgentMessage) (*types.AgentResponse, error) {
	agent, exists := m.GetAgent(agentID)
	if !exists {
		return nil, fmt.Errorf("agent not found: %s", agentID)
	}

	return agent.ProcessMessage(ctx, message)
}

// GoAgent is a basic Go implementation of an agent
type GoAgent struct {
	id           string
	name         string
	capabilities []string
}

// NewGoAgent creates a new Go agent
func NewGoAgent(name string) *GoAgent {
	return &GoAgent{
		id:   uuid.New().String(),
		name: name,
		capabilities: []string{
			"go_development",
			"microservices",
			"concurrent_programming",
			"api_development",
		},
	}
}

func (g *GoAgent) GetID() string {
	return g.id
}

func (g *GoAgent) GetName() string {
	return g.name
}

func (g *GoAgent) GetCapabilities() []string {
	return g.capabilities
}

func (g *GoAgent) ProcessMessage(ctx context.Context, message types.AgentMessage) (*types.AgentResponse, error) {
	logrus.Infof("Go agent %s processing message: %s", g.name, message.Content)

	// Simple echo response for now
	response := &types.AgentResponse{
		Success:   true,
		Data:      map[string]interface{}{"response": fmt.Sprintf("Go agent %s processed: %s", g.name, message.Content)},
		AgentID:   g.id,
		Timestamp: time.Now(),
	}

	return response, nil
}
EOF

cat > internal/api/routes.go << 'EOF'
package api

import (
	"net/http"
	"time"

	"github.com/gin-gonic/gin"
	"github.com/google/uuid"

	"agentic-go/internal/agents"
	"agentic-go/pkg/types"
)

// SetupRoutes configures all API routes
func SetupRoutes(router *gin.Engine, agentManager *agents.Manager) {
	v1 := router.Group("/api/v1")
	{
		v1.GET("/agents", listAgents(agentManager))
		v1.GET("/agents/:id", getAgent(agentManager))
		v1.POST("/agents/:id/process", processMessage(agentManager))
		v1.GET("/agents/:id/capabilities", getCapabilities(agentManager))
	}
}

func listAgents(manager *agents.Manager) gin.HandlerFunc {
	return func(c *gin.Context) {
		agents := manager.ListAgents()
		agentInfos := make([]map[string]interface{}, len(agents))

		for i, agent := range agents {
			agentInfos[i] = map[string]interface{}{
				"id":           agent.GetID(),
				"name":         agent.GetName(),
				"capabilities": agent.GetCapabilities(),
			}
		}

		c.JSON(http.StatusOK, gin.H{"agents": agentInfos})
	}
}

func getAgent(manager *agents.Manager) gin.HandlerFunc {
	return func(c *gin.Context) {
		id := c.Param("id")
		agent, exists := manager.GetAgent(id)

		if !exists {
			c.JSON(http.StatusNotFound, gin.H{"error": "Agent not found"})
			return
		}

		c.JSON(http.StatusOK, gin.H{
			"id":           agent.GetID(),
			"name":         agent.GetName(),
			"capabilities": agent.GetCapabilities(),
		})
	}
}

func processMessage(manager *agents.Manager) gin.HandlerFunc {
	return func(c *gin.Context) {
		id := c.Param("id")

		var req types.AgentMessage
		if err := c.ShouldBindJSON(&req); err != nil {
			c.JSON(http.StatusBadRequest, gin.H{"error": err.Error()})
			return
		}

		// Set message ID if not provided
		if req.ID == "" {
			req.ID = uuid.New().String()
		}

		// Set timestamp if not provided
		if req.Timestamp.IsZero() {
			req.Timestamp = time.Now()
		}

		response, err := manager.ProcessMessage(c, id, req)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": err.Error()})
			return
		}

		c.JSON(http.StatusOK, response)
	}
}

func getCapabilities(manager *agents.Manager) gin.HandlerFunc {
	return func(c *gin.Context) {
		id := c.Param("id")
		agent, exists := manager.GetAgent(id)

		if !exists {
			c.JSON(http.StatusNotFound, gin.H{"error": "Agent not found"})
			return
		}

		c.JSON(http.StatusOK, gin.H{
			"capabilities": agent.GetCapabilities(),
			"agent_id":     agent.GetID(),
			"agent_name":   agent.GetName(),
		})
	}
}
EOF

cat > pkg/types/types.go << 'EOF'
package types

import "time"

// AgentMessage represents a message sent to an agent
type AgentMessage struct {
	ID        string    `json:"id"`
	Role      string    `json:"role"`      // "user", "assistant", "system"
	Content   string    `json:"content"`
	Timestamp time.Time `json:"timestamp"`
}

// AgentResponse represents a response from an agent
type AgentResponse struct {
	Success   bool                   `json:"success"`
	Data      map[string]interface{} `json:"data,omitempty"`
	Error     string                 `json:"error,omitempty"`
	AgentID   string                 `json:"agent_id"`
	Timestamp time.Time              `json:"timestamp"`
}

// AgentConfig represents agent configuration
type AgentConfig struct {
	ID           string   `json:"id"`
	Name         string   `json:"name"`
	Description  string   `json:"description"`
	Capabilities []string `json:"capabilities"`
	Endpoint     string   `json:"endpoint,omitempty"`
}
EOF
