postCreateCommand:
cat > scripts/agents/agent-framework.nu << 'EOF'
#!/usr/bin/env nu

# Agentic Nushell Agent Framework
# Pipeline-based agent orchestration and communication

# Agent types
export def "agent types" [] {
  [
    "data_processor",
    "pipeline_orchestrator", 
    "script_generator",
    "automation_agent",
    "monitoring_agent"
  ]
}

# Agent configuration schema
export def "agent config schema" [] {
  {
    id: (random uuid)
    name: "string"
    type: "string"
    description: "string"
    capabilities: ["list", "of", "strings"]
    pipeline_config: {
      input_types: ["list"]
      output_types: ["list"]
      transformations: ["list"]
    }
    mcp_config: {
      server_url: "string"
      tools: ["list"]
    }
    created_at: (date now)
    status: "active"
  }
}

# Create a new agent
export def "agent create" [
  name: string
  type: string = "data_processor"
  --description: string = "Nushell agent"
  --capabilities: list = []
] {
  let agent_id = (random uuid)
  let config = {
    id: $agent_id
    name: $name
    type: $type
    description: $description
    capabilities: ($capabilities | append ["nushell_scripting", "data_processing", "pipeline_orchestration"])
    pipeline_config: {
      input_types: ["json", "csv", "toml", "yaml"]
      output_types: ["json", "table", "text"]
      transformations: ["filter", "map", "reduce", "group"]
    }
    mcp_config: {
      server_url: ($env.MCP_SERVER_URL? | default "http://localhost:3001")
      tools: ["environment_detect", "polyglot_check", "devpod_provision"]
    }
    created_at: (date now)
    status: "active"
  }

  # Save agent config
  $config | save $"config/agents/($agent_id).json"

  print $"âœ… Created agent: ($name) with ID: ($agent_id)"
  $config
}

# List all agents
export def "agent list" [] {
  if not ("config/agents" | path exists) {
    mkdir config/agents
    return []
  }

  ls config/agents/*.json 
  | get name 
  | each { |file| open $file }
  | select id name type status created_at
}

# Get agent by ID
export def "agent get" [id: string] {
  let config_file = $"config/agents/($id).json"
  if not ($config_file | path exists) {
    error make { msg: $"Agent not found: ($id)" }
  }
  open $config_file
}

# Process message through agent pipeline
export def "agent process" [
  agent_id: string
  message: record
  --format: string = "json"
] {
  let agent = (agent get $agent_id)
  let timestamp = (date now)

  # Create processing record
  let processing = {
    agent_id: $agent_id
    agent_name: $agent.name
    input: $message
    timestamp: $timestamp
    format: $format
  }

  # Process through pipeline
  let result = ($message 
    | upsert agent_processed_by $agent.name
    | upsert processed_at $timestamp
    | upsert capabilities $agent.capabilities
  )

  # Format output
  match $format {
    "json" => { $result | to json }
    "table" => { $result | table }
    "yaml" => { $result | to yaml }
    _ => { $result }
  }
}

# Agent pipeline operations
export def "pipeline create" [
  name: string
  agents: list
  --description: string = "Nushell agent pipeline"
] {
  let pipeline_id = (random uuid)
  let config = {
    id: $pipeline_id
    name: $name
    description: $description
    agents: $agents
    created_at: (date now)
    status: "ready"
  }

  $config | save $"config/pipelines/($pipeline_id).json"
  print $"âœ… Created pipeline: ($name) with ID: ($pipeline_id)"
  $config
}

# Run agent pipeline
export def "pipeline run" [
  pipeline_id: string
  data: any
  --output_format: string = "table"
] {
  let pipeline = (open $"config/pipelines/($pipeline_id).json")

  print $"ðŸš€ Running pipeline: ($pipeline.name)"

  let result = ($pipeline.agents 
    | reduce --fold $data { |agent_id, acc| 
        print $"  Processing through agent: ($agent_id)"
        agent process $agent_id $acc --format json | from json
      }
  )

  match $output_format {
    "json" => { $result | to json }
    "yaml" => { $result | to yaml }
    "table" => { $result | table }
    _ => { $result }
  }
}
EOF

cat > scripts/mcp/mcp-client.nu << 'EOF'
#!/usr/bin/env nu

# MCP Client for Nushell Agents
# Connects to the polyglot MCP server

export def "mcp connect" [--server: string = "http://localhost:3001"] {
  print $"ðŸ”— Connecting to MCP server: ($server)"

  let response = (http get $"($server)/health" | complete)

  if $response.status == 200 {
    print "âœ… MCP server is healthy"
    $response.stdout | from json
  } else {
    print "âŒ Failed to connect to MCP server"
    null
  }
}

export def "mcp call tool" [
  tool_name: string
  params: record = {}
  --server: string = "http://localhost:3001"
] {
  print $"ðŸ”§ Calling MCP tool: ($tool_name)"

  let payload = {
    method: "call_tool"
    params: {
      name: $tool_name
      arguments: $params
    }
  }

  let response = (http post $"($server)/tools/($tool_name)" {
    content-type: "application/json"
    body: ($payload | to json)
  } | complete)

  if $response.status == 200 {
    $response.stdout | from json
  } else {
    error make { msg: $"MCP tool call failed: ($response.stderr)" }
  }
}

export def "mcp list environments" [--server: string = "http://localhost:3001"] {
  mcp call tool "environment_detect" {} --server $server
}

export def "mcp provision devpod" [
  environment: string
  count: int = 1
  --server: string = "http://localhost:3001"
] {
  mcp call tool "devpod_provision" {
    environment: $environment
    count: $count
  } --server $server
}
EOF

cat > examples/data-processing-agent.nu << 'EOF'
#!/usr/bin/env nu

# Example: Data Processing Agent
# Demonstrates Nushell pipeline-based data processing

use ../scripts/agents/agent-framework.nu *

def main [] {
  print "ðŸ¤– Creating Data Processing Agent example"

  # Create a data processing agent
  let agent = (agent create "DataProcessor" "data_processor" 
    --description "Processes CSV and JSON data through Nushell pipelines"
    --capabilities ["csv_processing", "json_transformation", "data_validation"]
  )

  print $"Created agent: ($agent.name) with ID: ($agent.id)"

  # Example data to process
  let sample_data = [
    {name: "Alice", age: 30, department: "Engineering"}
    {name: "Bob", age: 25, department: "Marketing"}
    {name: "Carol", age: 35, department: "Engineering"}
  ]

  # Process data through agent
  let result = (agent process $agent.id {
    data: $sample_data
    operation: "group_by_department"
    format: "summary"
  })

  print "ðŸ“Š Processing result:"
  $result | from json | table
}

if ($env.PWD | path join "examples" | path exists) {
  main
}
EOF

cat > tests/agent-tests.nu << 'EOF'
#!/usr/bin/env nu

# Nushell Agent Test Suite

use ../scripts/agents/agent-framework.nu *

# Test agent creation
export def test_agent_creation [] {
  print "ðŸ§ª Testing agent creation..."

  let agent = (agent create "TestAgent" "data_processor" --description "Test agent")

  assert ($agent.name == "TestAgent")
  assert ($agent.type == "data_processor")
  assert ($agent.status == "active")

  print "âœ… Agent creation test passed"
}

# Test agent processing
export def test_agent_processing [] {
  print "ðŸ§ª Testing agent processing..."

  let agent = (agent create "ProcessorTest" "data_processor")
  let test_message = {content: "Hello, agent!", role: "user"}

  let result = (agent process $agent.id $test_message)

  assert ($result != null)
  print "âœ… Agent processing test passed"
}

# Test pipeline creation
export def test_pipeline_creation [] {
  print "ðŸ§ª Testing pipeline creation..."

  let agent1 = (agent create "PipelineAgent1" "data_processor")
  let agent2 = (agent create "PipelineAgent2" "data_processor")

  let pipeline = (pipeline create "TestPipeline" [$agent1.id, $agent2.id])

  assert ($pipeline.name == "TestPipeline")
  assert (($pipeline.agents | length) == 2)

  print "âœ… Pipeline creation test passed"
}

# Run all tests
export def run_all_tests [] {
  print "ðŸš€ Running Nushell agent test suite..."

  test_agent_creation
  test_agent_processing
  test_pipeline_creation

  print "ðŸŽ‰ All tests passed!"
}

if ($env.PWD | path join "tests" | path exists) {
  run_all_tests
}
EOF
