cat > src/agents/mod.rs << 'EOF'
use async_trait::async_trait;
use serde::{Deserialize, Serialize};
use crate::{Result, AgentError};

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AgentMessage {
    pub id: String,
    pub role: MessageRole,
    pub content: String,
    pub timestamp: chrono::DateTime<chrono::Utc>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum MessageRole {
    User,
    Assistant,
    System,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AgentResponse {
    pub success: bool,
    pub data: Option<serde_json::Value>,
    pub error: Option<String>,
    pub agent_id: String,
}

#[async_trait]
pub trait Agent: Send + Sync {
    async fn process_message(&self, message: AgentMessage) -> Result<AgentResponse>;
    async fn get_capabilities(&self) -> Vec<String>;
    fn get_id(&self) -> String;
    fn get_name(&self) -> String;
}

pub struct RustAgent {
    id: String,
    name: String,
    capabilities: Vec<String>,
}

impl RustAgent {
    pub fn new(name: String) -> Self {
        Self {
            id: uuid::Uuid::new_v4().to_string(),
            name,
            capabilities: vec![
                "rust_development".to_string(),
                "async_programming".to_string(),
                "performance_optimization".to_string(),
            ],
        }
    }
}

#[async_trait]
impl Agent for RustAgent {
    async fn process_message(&self, message: AgentMessage) -> Result<AgentResponse> {
        tracing::info!("Processing message: {}", message.content);

        // Simple echo response for now
        Ok(AgentResponse {
            success: true,
            data: Some(serde_json::json!({
                "response": format!("Rust agent {} processed: {}", self.name, message.content),
                "capabilities": self.capabilities
            })),
            error: None,
            agent_id: self.id.clone(),
        })
    }

    async fn get_capabilities(&self) -> Vec<String> {
        self.capabilities.clone()
    }

    fn get_id(&self) -> String {
        self.id.clone()
    }

    fn get_name(&self) -> String {
        self.name.clone()
    }
}
EOF

cat > src/bin/agent-server.rs << 'EOF'
use axum::{
    extract::{Path, State},
    http::StatusCode,
    response::Json,
    routing::{get, post},
    Router,
};
use std::sync::Arc;
use tokio::net::TcpListener;
use tower_http::cors::CorsLayer;
use tracing_subscriber;

use agentic_rust::{
    agents::{Agent, AgentMessage, RustAgent},
    config::AgentConfig,
    Result,
};

#[derive(Clone)]
struct AppState {
    agent: Arc<dyn Agent>,
    config: AgentConfig,
}

#[tokio::main]
async fn main() -> Result<()> {
    tracing_subscriber::init();

    let config = AgentConfig::default();
    let agent = Arc::new(RustAgent::new("Agentic Rust Server".to_string()));

    let state = AppState { agent, config };

    let app = Router::new()
        .route("/", get(health_check))
        .route("/health", get(health_check))
        .route("/agent/process", post(process_message))
        .route("/agent/capabilities", get(get_capabilities))
        .route("/agent/:id", get(get_agent_info))
        .layer(CorsLayer::permissive())
        .with_state(state);

    let listener = TcpListener::bind("0.0.0.0:8000").await?;
    tracing::info!("Agentic Rust server listening on 0.0.0.0:8000");

    axum::serve(listener, app).await?;

    Ok(())
}

async fn health_check() -> Json<serde_json::Value> {
    Json(serde_json::json!({
        "status": "healthy",
        "service": "agentic-rust",
        "version": "0.1.0"
    }))
}

async fn process_message(
    State(state): State<AppState>,
    Json(message): Json<AgentMessage>,
) -> Result<Json<serde_json::Value>, StatusCode> {
    match state.agent.process_message(message).await {
        Ok(response) => Ok(Json(serde_json::to_value(response).unwrap())),
        Err(_) => Err(StatusCode::INTERNAL_SERVER_ERROR),
    }
}

async fn get_capabilities(
    State(state): State<AppState>,
) -> Json<serde_json::Value> {
    let capabilities = state.agent.get_capabilities().await;
    Json(serde_json::json!({
        "capabilities": capabilities,
        "agent_id": state.agent.get_id(),
        "agent_name": state.agent.get_name()
    }))
}

async fn get_agent_info(
    Path(id): Path<String>,
    State(state): State<AppState>,
) -> Json<serde_json::Value> {
    Json(serde_json::json!({
        "id": id,
        "name": state.agent.get_name(),
        "capabilities": state.agent.get_capabilities().await,
        "status": "active"
    }))
}
EOF

cat > tests/integration_test.rs << 'EOF'
use agentic_rust::agents::{RustAgent, Agent, AgentMessage, MessageRole};

#[tokio::test]
async fn test_rust_agent_creation() {
    let agent = RustAgent::new("Test Agent".to_string());
    assert_eq!(agent.get_name(), "Test Agent");
    assert!(!agent.get_id().is_empty());
}

#[tokio::test]
async fn test_agent_message_processing() {
    let agent = RustAgent::new("Test Agent".to_string());

    let message = AgentMessage {
        id: uuid::Uuid::new_v4().to_string(),
        role: MessageRole::User,
        content: "Hello, agent!".to_string(),
        timestamp: chrono::Utc::now(),
    };

    let response = agent.process_message(message).await.unwrap();
    assert!(response.success);
    assert!(response.data.is_some());
}
EOF
