cat > src/lib/mcp-client.ts << 'EOF'
// MCP Client for connecting to the polyglot MCP server
export class MCPClient {
  private baseUrl: string;

  constructor(baseUrl = process.env.MCP_SERVER_URL || 'http://localhost:3001') {
    this.baseUrl = baseUrl;
  }

  async callTool(name: string, params: any) {
    const response = await fetch(`${this.baseUrl}/tools/${name}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(params),
    });
    return response.json();
  }

  async listEnvironments() {
    return this.callTool('environment_detect', {});
  }

  async provisionDevPod(environment: string, count = 1) {
    return this.callTool('devpod_provision', { environment, count });
  }
}

export const mcpClient = new MCPClient();
EOF

